mapper.delete:
DELETE FROM ua_t_account_proofreadError

	
		
			fromSystem=#{fromSystem}
		
		
			1=2
		
	
	
		
			AND proofreadType=#{proofreadType}
		
		
			AND 1=2
		
	
	
		
			AND proofreadDate=#{proofreadDate}
		
		
			AND 1=2
		
	
	
		
			AND channel=#{channel}
		
		
			AND 1=2

mapper.select:
select

from ua_t_account_proofreadError
  
	 
		 flowStatus in('01','02')
  	
	
		and errorType = #{errorType}
	
	
		and proofreadDate >=
		#{proofreadDateStart}
	
	
		and proofreadDate <=
		#{proofreadDateEnd}
	
	
		and channel = #{channel}
	
	
		and fromSystem =
		#{fromSystem}
	
	
		and
		proofreadType = #{proofreadType}
	
	
		and flowStatus = #{flowStatus}
	
	
		and
		businessOrderNum = #{businessOrderNum}
	
	
		and 	borrowNum = #{borrowNum}
	
  
order by proofreadDate desc

SELECT

FROM
ua_t_account_proofreadError
where
proofreadDate = #{proofreadDate}
and channel = #{channel}
and fromSystem=#{fromSystem}
and proofreadType =#{proofreadType}

	SELECT COUNT(id) as  proofreadCount,SUM(${proofreadField}) as proofreadTotalMoney ,CONCAT(#{proofreadField},'')  as proofreadType FROM ua_t_account_proofreadError WHERE  ${proofreadField} <> 0
		
			and errorType = #{errorType}
		
	    
	        and proofreadDate >=  #{proofreadDateStart}
        
        
	        and proofreadDate <=  #{proofreadDateEnd}
         
		
			and channel = #{channel}
		
		
			and fromSystem = #{fromSystem}
		
		
			and
			proofreadType = #{proofreadType}
		
		
			and flowStatus = #{flowStatus}
		
		
			and
			businessOrderNum = #{businessOrderNum}
		
	    
		   and 	borrowNum = #{borrowNum}

mapper.update:
UPDATE ua_t_account_proofreadError
disposeTime=NOW(),
remark = #{remark},
flowStatus = #{flowStatus},
updateUser = #{updateUser}
WHERE id = #{id}
