mapper.select:
SELECT

FROM
ua_t_account_proofreadSum
WHERE

	
		fromSystem=#{fromSystem}
	
	
		1=2
	


	
		AND proofreadType=#{proofreadType}
	
	
		1=2
	


	
		AND proofreadDate=#{proofreadDate}
	
	
		1=2

SELECT

FROM
ua_t_account_proofreadSum


	proofreadStatus=#{proofreadStatus}


	and DATE_FORMAT(proofreadDate,'%Y-%m-%d') = #{proofreadDate}
